mapper.select:
select no, i_name, content, cnt, tot, price, st_date, ed_date, img, categ, up_date from item
where st_date >= current_date and categ like #{categ}
order by no desc
limit #{limit} offset #{offset}

mapper.update:
update item
set cnt = cnt - 1
where no = #{no}

update item
set cnt = cnt + #{cnt}
where no = #{ref_ino}

update book
set cnt = #{cnt}
where pno = #{pno}

update item
set cnt = #{cnt}
where no = #{no}
